import { useState } from "react";
import "../../styles/layouts/NoteItem.scss";

const NoteItem = ({
  allNotes,
  newNote,
  allCategory,
  index,
  id,
  title,
  content,
  categoryTheme,
  handleDeleteNote,
  handleEditNote,
}) => {
  const [colorNote, setColorNote] = useState("#86E1E0");

  const handleToEditNote = () => {
    handleEditNote(index);
  };

  const handleToDeleteNote = () => {
    handleDeleteNote(index);
  };

  const noteCategoryThemre = allCategory.find(
    (eachCategoryTheme) => eachCategoryTheme.name === categoryTheme
  );

  return (
    <li
      className={`note__item ${
        newNote.title.length !== 0 ? "unactive" : "active"
      }`}
    >
      List
      <div
        className="note__item__container"
        style={{ background: noteCategoryThemre.colorNote }}
      >
        <div className="note__item__container__text">
          <small>#{categoryTheme}</small>
          <h3>
            {id} "" + "-" + "" {title}
          </h3>
          <p>{content}</p>
        </div>
        <span></span>
        <div>
          <i index={index} onClick={handleToEditNote}>
            icon edit-pencil
          </i>
          <i index={index} onClick={handleToDeleteNote}>
            icon trash
          </i>
        </div>
      </div>
    </li>
  );
};

export default NoteItem;
